;---------------------------------------BPUTSCXY         .BLOCK         JMP STARTBASCOLOR = $0286START    PHP         ;SAVE START ADDR         STA STRADDR         STY STRADDR+1         ;SAVE A,Y,X         PHA         TYA         PHA         TXA         PHA         ;SAVE ZPAGE1         LDA ZPAGE1         STA ZP1         LDA ZPAGE1+1  _         STA ZP1+1         ;SET ZPAGE1         LDA STRADDR+1         STA ZPAGE1+1         LDA STRADDR         STA ZPAGE1         ; SET Z TO ZPTR OFFSET 0         ;SAVE  CURRENT BASICCOLOR         LDA BASCOLOR         STA BC         LDY #$00         ; LOAD COLOR PARAM         LDA (ZPAGE1),Y         ; AND SET IT         STA BASCOLOR         ; ADJUSTING START ADDR         CLC         INC STRADDR         BCC NOREP1         INC STRADDR+1NOREP1   ; GET ADDRESS OF REMAINDER         LDA STRADDR         LDY STRADDR+1         ; PRINT STRING AT X,Y POS.         JSR BPUTSXY         ; RESTORING BASIC COLOR         LDA BC         STA BASCOLOR         ; REPLACING ZPAGE1 FOR BASIC         LDA ZP1+1         STA ZPAGE1+1         LDA ZP1         STA ZPAGE1         PLA         TAX         PLA         TAY         PLA         PLP         RTSSTRADDR  .WORD $00BC       .BYTE $00ZP1      .WORD $00         .BEND;---------------------------------------