;---------------------------------------; Nom fichier..: e-float.asm ; Type fichier.: (seq) equates pour T.M.P.; Auteur.......: Daniel Lafrance; Version......: 0.0.1 pour T.M.P. sept 6; Revision.....: 20251120;---------------------------------------; f1 = FAC1, f2 = FAC2, fv =FVAR;---------------------------------------; F.P. copy ;---------------------------------------ffvtf1   = $bba2 ;Copy fv $(YYAA) to f1.ffvtf2   = $ba8c ;Copy fv to f2.ff1t49   = $bbd0 ;Copy f1 to $49.ff1tme   = $bbd4 ;Copy f1 to $YYXX.ff1t5c   = $bbc7 ;Copy f1 to $5c-$60ff1t57   = $bbca ;Copy f1 to $57-$5bff2tf1   = $bbfc ;Copy f2 to f1ff1tf2   = $bc0c ;Copy f1 to f2;---------------------------------------; F.P. addition/substraction;---------------------------------------ff1pf2   = $b86a ;f1=f2+f1ff1pac   = $bd7e ;f1=f1+ra=(0-9)ff1pfv   = $b867 ;f1=fv+f1 ($yyaa)ff2sf1   = $b853 ;f1=f2-f1ffvsf1   = $b850 ;f1=fv-f1 ($yyaa)ff1p05   = $b849 ;f1=f1+0.5;---------------------------------------; F.P. Multiplication/division;---------------------------------------ff1xf2   = $ba2b ;f1=f1*f2ff1xfv   = $ba28 ;f1=f1*fv ($yyaa)ff1x10   = $bae2 ;f1=f1*10ff2df1   = $bb12 ;f1=f2/f1ffvdf1   = $bb0f ;f1=fv/f1 ($yyaa)ff1d10   = $bafe ;f1=f1/10;---------------------------------------; F.P. point Math and logic;---------------------------------------ff1rnd   = $bc1b ;f1=round(f1)ff1ef2   = $bf7b ;f1=f1^f2ff1sqr   = $bf71 ;f1=sqrt(f1)ffpand   = $afe9 ;f1=f1 and f2ffpor    = $afe6 ;f1=f1 or f2ff1int   = $bccc ;f1=int(f1)f1cplm2  = $b947 ;f1=twos compl f1ff1sign  = $bc39 ;f1=sgn(f1)ff1abs   = $bc58 ;f1=abs(f1)ffvcmp   = $bc5b ;f1=f1 comp fv ($yyaa);---------------------------------------; Integer to F.P. Conversion;---------------------------------------fi62tf1  = $bc44 ;Conv f1 word 62,63fiaatf1  = $bc3b ;Conv Acc to FP in f1fixatf1  = $bdcd ;f1=float($XXAA)+printfiyytf1  = $b3a2 ;f1=float(y);---------------------------------------; Decimal to floating point;---------------------------------------fasctf1  = $bcf3 ;f1=float(ascii) $0200;---------------------------------------; F.P. to ascii.;---------------------------------------ff1tasc  = $bddd ;f1 to ascii $0100;---------------------------------------; F.P. to fixed point integer;---------------------------------------ff1tudw  = $bc9b ;f1=unsighed dw(f1)ff1swd64 = $b1bf ;f1=sw(f1)->64ff1wrd64 = $b1d2 ;f1=uw(f1)->64ff1wrday = $b1aa ;fa=word->$aayyff1bytxx = $b7a1 ;f1 to byte in xff1evlx  = $b79e ;Eval. expr. f1 to x;---------------------------------------; F.P. Trigonometry RAD;---------------------------------------ff1cos   = $e264 ;f1=sin(f1+(pi/2))ff1sin   = $e26b ;f1=sin(f1)ff1tan   = $e2b4 ;f1=sin(f1)/cos(f1)ff1atn   = $e30e ;f1=atn(f1)fsngf1   = $bc2b ;check f1 sign;---------------------------------------