   rtsnumbytes .byte 0        .bend comaputs        .block        jsr pushall        ldy #$00next    lda (zp1),y        cmp #44        beq out        jsr chrout        iny        jmp nextout     jsr popall        rts        .bendtestprg jsr pushregs        lda #$05        sta $c002        lda $c002,y        jsr popregs        rts;---------------------------------------; Les fichiers du code du programme.;---------------------------------------        .include "memdump.asm"        .include "getadr.asm"        .include "ramprobe.asm"        .include "string-fr.asm";        .include "string-en.asm";---------------------------------------; Les librairies generales.;---------------------------------------        .include "l-screen.asm"        .include "l-push.asm"        .include "l-mem.asm"        .include "l-conv.asm"        .include "l-string.asm"        .include "l-float.asm"        .include "l-keyb.asm"        .include "l-math.asm"        .include "l-bitmap.asm"        .include "l-drawbox.asm"        .include "l-opmneumo.asm"        .include "l-opcodes.asm"        .include "l-opcycles.asm"        .include "l-oplenght.asm"        .include "l-opmodes.asm" ;---------------------------------------