;--------------------------------------; fichier......: e-vars.asm (seq); type fichier.: equates; auteur.......: Daniel Lafrance; version......: 0.0.2; revision.....: 20151208;--------------------------------------; Adresse haute des variables; Les variables dans $9???;--------------------------------------;libre    = $8000   ; *+3085libre    = maxram   ; *+3085;--------------------------------------; variables pour l-push.asm    8 bytes;--------------------------------------rp       = libre-$01;1 byte $cfffra       = rp-1     ;1 byte $cfferx       = ra-1     ;1 byte $cffdry       = rx-1     ;1 byte $cffcrs       = ry-1     ;1 byte $cffbpc       = rs-2     ;2 byte $cff9-$cffacurcol   = pc-1     ;1 byte $cff6;--------------------------------------; variables pour l-mem.asm     7 bytes;--------------------------------------memptr   = curcol-1;1 word $cff7-$cff8src      = memptr-2dst      = memptr-4loopcount = memptr-6;--------------------------------------; variables pour l-conv.asm   33 bytes    ;--------------------------------------hexv     = loopcounthexstr   = hexv-$05 ;5 byte a2hexpfx = hexv-$06 ;1 byte a2hexpy  = hexv-$07 ;1 byte a2hexpx  = hexv-$08 ;1 byte a2hexpos = hexv-$08 ;0 bytea2hexcol = hexv-$09 ;1 byte;--------------------------------------binv     = a2hexcolbinstr   = binv-$09 ;9 bytea2binpfx = binv-$0a ;1 bytea2binpy  = binv-$0b ;1 bytea2binpx  = binv-$0c ;1 bytea2binpos = binv-$0c ;0 bytea2bincol = binv-$0d ;1 byte;--------------------------------------decv     = a2bincoldecstr   = decv-$06 ;6 bytea2decpfx = decv-$07 ;1 bytea2decpy  = decv-$08 ;1 bytea2decpx  = decv-$09 ;1 bytea2decpos = decv-$09 ;0 bytea2deccol = decv-$0a ;1 byteoffset   = decv-$0b ;1 byte;--------------------------------------; variables pout l-float.asm  55 bytes;--------------------------------------fltv     = offsetfvar1    = fltv-6  ; 6 bytefvar2    = fltv-12 ; 6 bytefloat1   = fltv-32 ;20 bytefloat2   = fltv-52 ;20 bytetval1    = fltv-53 ; 1 bytetval2    = fltv-55 ; 2 byte;--------------------------------------;variables pour l-math.asm     2 bytes;--------------------------------------reponse  = tval2-2 ; 2 byte;--------------------------------------; Variables pour l-drawbox     9 bytes;--------------------------------------edbox    = reponsecleft    = edbox-1 ;1 bytecright   = edbox-2 ;1 bytecmid     = edbox-3 ;1 bytedbtop    = edbox-4 ;1 bytedbleft   = edbox-5 ;1 bytedbwdth   = edbox-6 ;1 bytedbhght   = edbox-7 ;1 bytedbcoul   = edbox-8 ;1 bytedbclin   = edbox-9 ;1 byte;--------------------------------------; Variables pout l-keyb.asm    3 bytes;--------------------------------------ekeyb    = dbclincurx     = ekeyb-1 ;1 bytecury     = ekeyb-2 ;1 byteclef     = ekeyb-3 ;1 byte;--------------------------------------; Variables pout l-string.asm  4 bytes;--------------------------------------len      = clef-1  ;1 bytecx       = clef-2  ;1 bytecy       = clef-3  ;1 bytebcol     = clef-4  ;1 byte;--------------------------------------escr     = bcol         ;2051 bytesloops    = escr-1       ;   1 byteloop16   = loops-2      ;   2 bytescrncar  = loop16-$200  ;  1k bytescrncol  = scrncar-$200 ;  1k byte;--------------------------------------; Variables pour l-disk.asm   13 bytes;--------------------------------------diskvar  = scrncolddatas   = diskvar-2 ;2 byte Data startddatae   = diskvar-4 ;2 byte Data end ddev     = diskvar-5 ;1 byte Dev numberdlfsno   = diskvar-6 ;1 byte Lfsdfnptr   = diskvar-8 ;2 byte Ptrnfichierdfnlen   = diskvar-9 ;1 byte lenfilename;--------------------------------------genbyte1 = dfnlen-1 ; 1 bytegenword1 = dfnlen-3 ; 2 bytefreevar  = genword1-1;--------------------------------------