;--------------------------------------; fichier......: e-vars.asm (seq); type fichier.: equates; auteur.......: Daniel Lafrance; version......: 0.0.2; revision.....: 20151208;--------------------------------------; Adresse haute des variables;--------------------------------------libre    = $d000;--------------------------------------; variables pour l-push.asm;--------------------------------------rp       = libre-$01;1 byte $cfffra       = rp-1     ;1 byte $cfferx       = ra-1     ;1 byte $cffdry       = rx-1     ;1 byte $cffcrs       = ry-1     ;1 byte $cffbpc       = rs-2     ;2 byte $cff9-$cffacurcol   = pc-1     ;1 byte $cff6;--------------------------------------; variables pour l-mem.asm;--------------------------------------memptr   = curcol-1;1 word $cff7-$cff8;--------------------------------------; variables pour l-conv.asm;--------------------------------------hexv     = memptrhexstr   = hexv-$05 ;5 byte $cff1-$cff5a2hexpfx = hexv-$06 ;1 byte $cff0a2hexpy  = hexv-$07 ;1 byte $cfefa2hexpx  = hexv-$08 ;1 byte $cfeea2hexpos = hexv-$08 ;0 byte $cfeea2hexcol = hexv-$09 ;1 byte $cfed;--------------------------------------binv     = a2hexcolbinstr   = binv-$09 ;9 byte $cfe4-$cfeca2binpfx = binv-$0a ;1 byte $cfe3a2binpy  = binv-$0b ;1 byte $cfe2a2binpx  = binv-$0c ;1 byte $cfe1a2binpos = binv-$0c ;0 byte $cfe1a2bincol = binv-$0d ;1 byte $cfe0;--------------------------------------decv     = a2bincoldecstr   = decv-$06 ;6 byte $cfda-$cfdfa2decpfx = decv-$07 ;1 byte $cfd9a2decpy  = decv-$08 ;1 byte $cfd8a2decpx  = decv-$09 ;1 byte $cfd7a2decpos = decv-$09 ;0 byte $cfd7a2deccol = decv-$0a ;1 byte $cfd6offset   = decv-$0b ;1 byte $c799;--------------------------------------; variables pout l-float.asm;--------------------------------------fltv     = offsetfvar1    = fltv-6  ; 6 byte $cfd0-$cfd5fvar2    = fltv-12 ; 6 byte $cfca-$cfcffloat1   = fltv-32 ;20 byte $cfb6-$cfc9float2   = fltv-52 ;20 byte $cfa2-$cfb5tval1    = fltv-53 ; 1 byte $cfa1tval2    = fltv-55 ; 2 byte $cf9f-$cfa0;--------------------------------------;variables pour l-math.asm;--------------------------------------reponse  = tval2-2      ; 2 byte $cf9d;--------------------------------------; Variables pour l-drawbox;--------------------------------------edbox    = reponsecleft    = edbox-1cright   = edbox-2cmid     = edbox-3dbtop    = edbox-4dbleft   = edbox-5dbwdth   = edbox-6dbhght   = edbox-7dbcoul   = edbox-8dbclin   = edbox-9;--------------------------------------; Variables pout l-keyb.asm;--------------------------------------ekeyb    = dbclincurx     = ekeyb-1cury     = ekeyb-2clef     = ekeyb-3;--------------------------------------; Variables pout l-string.asm;--------------------------------------len      = clef-1;--------------------------------------escr     =  len     loops    = escr-1       ; 1 byte $cf9cloop16   = loops-2      ; 2 byte $cf9ascrncar  = loop16-1024  ;1k byte $cb9ascrncol  = scrncar-1024 ;1k byte $c79a;--------------------------------------nextvar  = scrncol-1    ; 1 byte $c799;--------------------------------------