;---------------------------------------; fichier......: m-utils.asm (seq); type fichier.: macros; auteur.......: daniel lafrance; version......: 0.0.1; revision.....: 20151126;---------------------------------------; Affichage d'un caractere par kernal;---------------------------------------outcar  .macro carno        php        pha        lda #\carno        jsr chrout        pla        plp        .endm;---------------------------------------; Affiche une chaine par le kernal;---------------------------------------outstr  .macro string        jsr pushregs        ldy #>\string+1        ldx #<\string        jsr putsyx        jsr popregs        .endm;---------------------------------------; charge le contenu du ptr dans $YYXX;---------------------------------------ldyxptr .macro pointeur        php        ldy \pointeur+1        ldx \pointeur        plp        .endm;---------------------------------------; charge la valeur 16 bits dans $yyxx;---------------------------------------ldyximm .macro immval        php        ldy #>\immval        ldx #<\immval        plp        .endm;---------------------------------------